mapper.update:
UPDATE users
SET userName = #{userName}
WHERE id = #{id}

UPDATE users
SET email = #{email}
WHERE id = #{id}

mapper.select:
SELECT id, userName, password, code, role, email
FROM users
WHERE userName = #{userName}

SELECT count(*)
FROM users
WHERE userName = #{userName}

UPDATE users
SET password = #{newPassword}
WHERE id = #{id}
